function loadGameWeeks(){var b=39;for(var a=1;a<b;a++){$("#gameWeekSelect").append($("<option>",{value:a,text:"Game Week "+a}))}}function loadFixtures(b,d){var a="https://api.football-data.org/v1/competitions/"+b+"/fixtures?matchday="+d;var c=$("#footballCentre").find("nav .nav-link.active").text();$("#fixturesSection").find("h1").text(c+" Week "+d+" Fixtures");$("#fixturesSection").find("table tbody:last-child").find("tr:gt(0)").remove();$.ajax({type:"GET",headers:{"X-Auth-Token":"eef44c20bd9e468597d2c7c85d88eb46"},url:"sample_data/fixtures.json",dataType:"json",success:function(e){var f=e.fixtures;$.each(f,function(i,k){var l=k.homeTeamName;if(k.status==="SCHEDULED"){var j=" ";var g=" "}else{var j=k.result["goalsHomeTeam"];var g=k.result["goalsAwayTeam"]}var h=k.awayTeamName;$("#fixturesSection").find("table tbody:last-child").append("<tr><td>"+l+"</td><td>"+j+"</td><td>vs</td><td>"+g+"</td><td>"+h+"</td></tr>")})},error:function(){console.log("Error")}})}function loadLeagueTable(a){var b=$("#footballCentre").find("nav .nav-link.active").text();$("#leagueTableSection").find("h1").text(b+" Table");$("#leagueTableSection").find("table tbody:last-child").find("tr:gt(0)").remove();$.ajax({type:"GET",headers:{"X-Auth-Token":"eef44c20bd9e468597d2c7c85d88eb46"},url:"sample_data/prem_table.json",dataType:"json",success:function(d){var c=d.standing;$.each(c,function(k,l){var j=l.position;var e=l.teamName;var i=l.playedGames;var g=l.wins;var h=l.draws;var n=l.losses;var f=l.goalDifference;var m=l.points;$("#leagueTableSection").find("table tbody:last-child").append("<tr><td>"+j+"</td><td>"+e+"</td><td>"+i+"</td><td>"+g+"</td><td>"+h+"</td><td>"+n+"</td><td>"+f+"</td><td>"+m+"</td></tr>")})},error:function(){console.log("Errro")}})}$(document).ready(function(){$(function(){$("#footballCentre").tabs();$("#gameWeekSpinner").spinner()});loadGameWeeks();loadFixtures(445,1);loadLeagueTable(445);$("#footballCentre").find("nav .nav-link").click(function(){var a=$(this).data("target");$(this).addClass("active").siblings().removeClass("active");loadLeagueTable(a);loadFixtures(a,1)});$("#submitGameWeek").click(function(){var a=$("#gameWeekSpinner").val();var b=$("#footballCentre").find("nav .nav-link.active").data("target");loadFixtures(b,a)});$("#footballCentre").find("ul li a").click(function(){$("#gameWeekForm").toggle()})});